import React, { useState, useEffect } from 'react';

function App() {
  const [screen, setScreen] = useState('welcome');
  const [anim, setAnim] = useState(null); // null | 'yes' | 'no'

  // Функция для кнопки Activate
  const handleActivate = () => {
    if (anim) return;
    setAnim(Math.random() < 0.5 ? 'yes' : 'no');
  };

  useEffect(() => {
    if (screen === 'welcome') {
      const timer = setTimeout(() => setScreen('main'), 3000);
      return () => clearTimeout(timer);
    }
  }, [screen]);

  // Логика анимации
  useEffect(() => {
    if (anim) {
      const timer = setTimeout(() => setAnim(null), 3800); // длительность gif увеличена до 3.8 секунд
      return () => clearTimeout(timer);
    }
  }, [anim]);

  if (screen === 'welcome') {
    return (
      <div className="bg-image" style={{ backgroundImage: "url('/bg1.png')" }}>
        <div style={{ textAlign: 'center' }}>
          <h1>Да-да, Нет-нет</h1>
          <div className="subtitle">made for mashkaxd</div>
        </div>
      </div>
    );
  }



  return (
    <div className="bg-image2" style={{ backgroundImage: "url('/bg2.png')" }}>
      <div style={{ position: 'relative', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        {/* 8ball или анимация */}
        {!anim && (
          <img src="/8ball.png" alt="8 Ball" style={{ maxWidth: 320, maxHeight: 320, objectFit: 'contain', display: 'block' }} />
        )}
        {anim === 'yes' && (
          <img src="/anim_yes.gif" alt="anim_yes" style={{ maxWidth: 320, maxHeight: 320, objectFit: 'contain', display: 'block' }} />
        )}
        {anim === 'no' && (
          <img src="/anim_no.gif" alt="anim_no" style={{ maxWidth: 320, maxHeight: 320, objectFit: 'contain', display: 'block' }} />
        )}
        {/* Кнопка Activate */}
        <img
          src="/activate.png"
          alt="Activate"
          style={{ marginTop: 40, maxWidth: 220, height: 'auto', display: anim ? 'none' : 'block', cursor: 'pointer' }}
          onClick={handleActivate}
        />
      </div>
    </div>
  );
}

export default App;
