import React, { useState, useEffect } from 'react';

const MainScreen = ({ onActivate }) => {
  const [animState, setAnimState] = useState('idle'); // idle | playing | result
  const [result, setResult] = useState(null);

  const handleClick = () => {
    if (animState === 'playing') return;
    // choose random result
    const pick = Math.random() < 0.5 ? 'ДА' : 'НЕТ';
    setResult(pick);
    setAnimState('playing');

    // duration of gif animation (adjust as needed)
    const animDuration = 1600;
    setTimeout(() => {
      setAnimState('result');
      if (typeof onActivate === 'function') onActivate(pick);
    }, animDuration);
  };

  useEffect(() => {
    if (animState === 'result') {
      // hide result after a while
      const t = setTimeout(() => {
        setAnimState('idle');
        setResult(null);
      }, 2500);
      return () => clearTimeout(t);
    }
  }, [animState]);

  return (
    <div
      className="bg-image2"
      style={{
        backgroundImage: "url('/bg2.png')",
        minHeight: "100vh",
        width: "100vw",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center"
      }}
    >

      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', width: '100%', height: '100%' }}>
        {/* show 8ball or animation */}
        {animState === 'idle' && (
          <img src="/8ball.png" alt="8 Ball" style={{ maxWidth: 430, maxHeight: 234, objectFit: 'contain', display: 'block' }} />
        )}

        {animState === 'playing' && (
          <img src={result === 'ДА' ? '/anim_yes.gif' : '/anim_no.gif'} alt="anim" style={{ maxWidth: 860, maxHeight: 468, objectFit: 'contain', display: 'block' }} />
        )}

        {animState === 'result' && result && (
          <div className="result-overlay">
            <div className={`result-text result-${result === 'ДА' ? 'yes' : 'no'}`}>
              {result}
            </div>
          </div>
        )}
      </div>

      <button
        className="activate-button"
        onClick={handleClick}
        aria-label="Activate"
      >
        Activate
      </button>
    </div>
  );
};

export default MainScreen;
