import React, { useState, useEffect } from 'react';

const MainScreen = ({ onActivate }) => {
  const [animState, setAnimState] = useState('idle'); // idle | playing | result
  const [result, setResult] = useState(null);

  const handleClick = () => {
    if (animState !== 'idle') return;
    // choose random result
    const pick = Math.random() < 0.5 ? 'ДА' : 'НЕТ';
    setResult(pick);
    setAnimState('playing');

    // duration of gif animation (adjust as needed)
    const animDuration = 5000;
    setTimeout(() => {
      setAnimState('result');
      if (typeof onActivate === 'function') onActivate(pick);
    }, animDuration);
  };

  useEffect(() => {
    if (animState === 'result') {
      // hide result after a while
      const t = setTimeout(() => {
        setAnimState('idle');
        setResult(null);
      }, 2500);
      return () => clearTimeout(t);
    }
    // Не сбрасывать результат при playing
  }, [animState]);

  return (
    <div
      className="bg-image2"
      style={{
        backgroundImage: "url('/bg2.png')",
        minHeight: "100vh",
        width: "100vw",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center"
      }}
    >

      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', width: '100%', height: '100%' }}>
        {/* show 8ball or animation */}
        {animState === 'idle' && (
          <img src="/8ball.png" alt="8 Ball" style={{ maxWidth: 520, maxHeight: 282, objectFit: 'contain', display: 'block' }} />
        )}

        {animState === 'playing' && (
          <div>
            <div style={{ color: '#fff', fontSize: 16, marginBottom: 8 }}>result: {String(result)}</div>
            {result === 'ДА' ? (
              <FrameAnimation folder="/anim_yes_frames/" frameCount={113} fps={24} onEnd={() => setAnimState('result')} />
            ) : (
              <FrameAnimation folder="/anim_no_frames/" frameCount={113} fps={24} onEnd={() => setAnimState('result')} />
            )}
          </div>
        )}

        {animState === 'result' && result && (
          <div className="result-overlay">
            <div className={`result-text result-${result === 'ДА' ? 'yes' : 'no'}`}>
              {result}
            </div>
          </div>
        )}
      </div>

      {/* Кнопка Activate */}
      <img
        src="/activate.png"
        alt="Activate"
        style={{ marginTop: 40, maxWidth: 220, height: 'auto', display: animState === 'playing' ? 'none' : 'block', cursor: 'pointer' }}
        onClick={handleClick}
      />
    </div>
  );
};

export default MainScreen;
