import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [screen, setScreen] = useState('welcome');

  useEffect(() => {
    if (screen === 'welcome') {
      const timer = setTimeout(() => setScreen('main'), 3000);
      return () => clearTimeout(timer);
    }
  }, [screen]);

  if (screen === 'welcome') {
    return (
      <div
        className="bg-image"
        style={{
          backgroundImage: "url('/bg1.png')",
          minHeight: "100vh",
          width: "100vw",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center"
        }}
      >
        <h1>Да-да, Нет-нет</h1>
      </div>
    );
  }

  // Второй экран
  return (
    <div
      className="bg-image2"
      style={{
        backgroundImage: "url('/bg2.png')",
        minHeight: "100vh",
        width: "100vw",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center"
      }}
    >
      <img src="/8ball.png" alt="8 Ball" style={{ maxWidth: 320, maxHeight: 320, objectFit: 'contain', display: 'block' }} />
      <img src="/activate.png" alt="Activate" style={{ marginTop: 40, maxWidth: 220, height: 'auto', display: 'block' }} />
    </div>
  );
}

export default App;
