import React, { useState, useEffect } from 'react';
import MainScreen from './MainScreen';

function App() {
    const [animKey, setAnimKey] = useState(Date.now());
    const [gifSrc, setGifSrc] = useState('');
  const [screen, setScreen] = useState('welcome');
  const [anim, setAnim] = useState(null); // null | 'yes' | 'no'

  // Функция для кнопки Activate
  const handleActivate = () => {
    if (anim) return;
    const result = Math.random() < 0.5 ? 'yes' : 'no';
    setAnim(result);
    const src = result === 'yes' ? `/anim_yes.gif?t=${Date.now()}` : `/anim_no.gif?t=${Date.now()}`;
    setGifSrc(src);
    setAnimKey(Date.now());
  };

  useEffect(() => {
    if (screen === 'welcome') {
      const timer = setTimeout(() => setScreen('main'), 3000);
      return () => clearTimeout(timer);
    }
  }, [screen]);

  // Логика анимации
  useEffect(() => {
    if (anim) {
      const timer = setTimeout(() => setAnim(null), 3800); // длительность gif увеличена до 3.8 секунд
      return () => clearTimeout(timer);
    }
  }, [anim]);

  if (screen === 'welcome') {
    return (
      <div className="bg-image" style={{ backgroundImage: "url('/bg1.png')" }}>
        <div style={{ textAlign: 'center' }}>
          <h1>Да-да, Нет-нет</h1>
          <div className="subtitle">made for mashkaxd</div>
        </div>
      </div>
    );
  }

  // Новый основной экран с покадровой анимацией
  if (screen === 'main') {
    return <MainScreen />;
  }
}

export default App;
